            &nbsp;
        </td>

    </tr>
</table>
<nobr>
    <div style="display: inline">
        <div id="language">Fast Companies â™¥ Decentralization</div>
    </div>
    <div style="display: inline"><img id="language" src=""></div>
</nobr>
<br>
<script>

    var lang = new React.LS('languages/de.json', 'de_DE');

    lang.ready(function () {
        var rlsui_languages = [
            {
                id: 'en_US',
                langfile: 'languages/en.json',
                title: 'English (US)',
                name: ' English (US)',
                flagImg: 'images/flags/us.png',
                flagTitle: 'United States'
            },
            {
                id: 'de_DE',
                langfile: 'languages/de.json',
                title: 'German',
                name: ' Deutsch',
                flagImg: 'images/flags/de.png',
                flagTitle: 'Germany'
            },
        ];

        React.render(React.createElement(lang.UI, {
            items: rlsui_languages,
            selectedLang: window.lang.getsetting('lang', 'de_DE'),
            onLanguageChanged: function (rlsui_lang) {

                lang.set_load_lang(rlsui_lang.id, rlsui_lang.langfile);
            }
        }), document.getElementById('rlsui1'));
    });
</script>
